INSERT INTO `opixer_bookmark` VALUES ('190', 'http://codeigniter.org.cn', 'CodeIgniter', '0', '#BA68C8', 'CodeIgniter 是一个小巧但功能强大的 PHP 框架，作为一个简单而“优雅”的工具包，它可以为开发者们建立功能完善的 Web 应用程序。', null, '106897', 'http://www.opixer.com/var/thumb/983a3204837f7eae958f2eef85db7375-600-300.jpg', '1500553091');
