INSERT INTO `opixer_bookmark` VALUES ('243', 'http://littlesnippets.net/', 'LittleSnippets.net', '0', '#A1887F', 'LittleSnippets.net是CSS3/ HTML片段博客来激发你的网站设计。 100的免费的集种植使用与制作和日常添加新条目的片段。\n\n要不断更新这些新的项目，因为它们发生一定要按照我们在Facebook或Twitter。您还可以检查我们在上Dribbble。', null, '104594', 'http://www.opixer.com/var/thumb/af46c116a6e448ce544fa8a7779f70ce-600-300.jpg', '1500553091');
