INSERT INTO `opixer_bookmark` VALUES ('365', 'https://avocode.com/nachos-ui/', 'Nachos UI Kit', '1', '#FF8A65', 'Nachos UI Kit 是一款非常优秀的预编程的UI组件库，可以在你完成设计的同时快速创建应用程序，你所需要做的就是安装，然后导入你所喜欢的组件就行。', null, '106880', 'http://www.opixer.com/var/thumb/8539f8ff194c194ff8c2b67281534c4d-600-300.jpg', '1500553091');
