INSERT INTO `opixer_bookmark` VALUES ('872', 'http://cn.vuejs.org', 'Vue.js(中文网)', '0', '#F06292', 'Vue.js是一套构建用户界面的渐进式框架。与其他重量级框架不同的是，Vue 采用自底向上增量开发的设计。Vue 的核心库只关注视图层，它不仅易于上手，还便于与第三方库或既有项目整合。另一方面，当与单文件组件和 Vue 生态系统支持的库结合使用时，Vue 也完全能够为复杂的单页应用程序提供驱动。', null, '105910', 'http://www.opixer.com/var/thumb/1b1bf0374699f78c4862eb3bbea0860a-600-300.jpg', '1500553091');
