INSERT INTO `opixer_bookmark` VALUES ('874', 'http://www.muse-ui.org/#/index', 'Muse UI', '0', '#F06292', 'Muse UI 是一款基于 Vue.js 2.0 采用Material Design 风格的 UI 库，其中包含了诸如 Appbar、Card 和 Dialog 这样典型的Material design 风的控件。在它的基础上快速构建UI是个不错的选择。', null, '75477', 'http://www.opixer.com/var/thumb/f0df3a636cd88516cab708a0dee447b6-600-300.jpg', '1500553091');
