INSERT INTO `opixer_bookmark` VALUES ('888', 'http://cssreference.io/', 'CSS Reference', '0', '#4DB6AC', '上方 Collections 收录一些 CSS 主题，例如动画、背景、容器模型、Flexbox、位置、过场动画及排版，下方则有搜寻框及所有 CSS 属性，目前一共收录 129 个。', null, '106877', 'http://www.opixer.com/var/thumb/bd3fc42047b0625b15bff22b36f3f23d-600-300.jpg', '1500553091');
