INSERT INTO `opixer_bookmark` VALUES ('907', 'http://www.w3cfuns.com/tools.php', 'w3cfuns tools', '0', '#64B5F6', '前端工具库，简单、直观、强悍的前端工具仓库，让Web前端开发更迅速。', null, '106626', 'http://www.opixer.com/var/thumb/5c87c2b4c642eae2dd9c6ae44c33a6fc-600-300.jpg', '1500553091');
